import { API_CLIENT } from '../../../Api/API_Client';

// Fetch all vendors
export const fetchVendorsApi = async () => {
  const response = await API_CLIENT.get('/api/vendors/all-vendors');
  return response.data;
};

// Create a new vendor
export const createVendorApi = (formData) => {
  return API_CLIENT.post('/api/vendors', formData);
};

// Update a vendor by ID
export const updateVendorApi = (vendorId, formData) => {
  return API_CLIENT.put(`/api/vendors/${vendorId}`, formData);
};
